mapper.select:
SELECT COUNT(*) FROM MEMBER
	WHERE
ID = #{id}

mapper.insert:
    	INSERT 
    		INTO
    	MEMBER (
    			ID
    			, NAME
    			, PASSWORD
    			, REGISTER_DATE

    				, PHONE


    				, SEX


    				, EMAIL

    			, IP
    			, LOGIN_DATE
    			, CHANGE_DATE

    				, ADDRESS_SEQ

    			)
    		VALUES
	    		(
	    		#{id}
	    		, #{name}
	    		, #{password}
	    		, current_timestamp

	    			, #{phone}


	    			, #{sex}


	    			, #{email}

	    		, '127.0.0.1'
	    		, current_timestamp
	    		, current_timestamp

	    			,#{address_seq}

	    		)
